commit-types.description:
List of commit types used for version calculation and changelog generation.
Default: [
  {
    "type": "feat",
    "section": "Features"
  },
  {
    "type": "fix",
    "section": "Bug Fixes"
  },
  {
    "type": "perf",
    "section": "Performance Improvements"
  },
  {
    "type": "revert",
    "section": "Reverts"
  }
]

major.description:
Strategy for bumping major version (x.2.3).
Default: {
  "types": [],
  "count-breaking-as": "bump",
  "commits-per-bump": 1
}

minor.description:
Strategy for bumping minor version (1.x.3).
Default: {
  "types": [
    "feat"
  ],
  "commits-per-bump": 1
}

patch.description:
Strategy for bumping patch version (1.2.x).
Default: {
  "types": [
    "fix",
    "perf"
  ],
  "commits-per-bump": 1
}

labels-on-create.description:
A label or an array of labels to add to the pull request when it is created.
Default: {
  "name": "release: pending",
  "description": "Indicates that the pull request is pending release.",
  "color": "FBCA04"
}

labels-on-close.description:
A label or an array of labels to add to the pull request when it is closed and the release operation has completed.
Default: {
  "name": "release: released",
  "description": "Indicates that the pull request is closed and the release has completed.",
  "color": "0E8A16"
}
