commit-types.description:
List of commit types used for version calculation and changelog generation.
Default: [
  {
    "type": "feat",
    "section": "Features"
  },
  {
    "type": "fix",
    "section": "Bug Fixes"
  },
  {
    "type": "perf",
    "section": "Performance Improvements"
  },
  {
    "type": "revert",
    "section": "Reverts"
  }
]

major.description:
Strategy for bumping major version (x.2.3).
Default: {
  "count-breaking-as": "bump"
}

minor.description:
Strategy for bumping minor version (1.x.3).
Default: {
  "types": [
    "feat"
  ]
}

patch.description:
Strategy for bumping patch version (1.2.x).
Default: {
  "types": [
    "fix",
    "perf"
  ]
}

on-create.description:
Label to add when pull request is created.
Default: {
  "name": "release: pending",
  "description": "Indicates that the pull request is pending release.",
  "color": "FBCA04"
}

on-close.description:
Label to add when pull request is closed and release operation has completed (replaces `on-create` label).
Default: {
  "name": "release: released",
  "description": "Indicates that the pull request is closed and the release has completed.",
  "color": "0E8A16"
}
